public class SrvProductManagerImpl implements ISrvProductManager {
	
	/** The srvProductdao. */
	IBaseDAO baseDAO;
	
	/** The getcmspk. */
	IGetPK getcmspk;
	
	/* (non-Javadoc)
	 * @see com.soaplat.sysmgr.manageriface.IBaseManager#delete(java.lang.Object[])
	 */
	public void delete(Object[] object) {
		//��ɾ��
		if(object.length>0){
			for(int i=0;i<object.length;i++){
				baseDAO.delete(object[i]);
			}
		}else{
			return ;
		}	
	}

	/* (non-Javadoc)
	 * @see com.soaplat.sysmgr.manageriface.IBaseManager#deleteById(java.lang.String)
	 */
	public void deleteById(String pkid) {

		SrvProduct srvProduct=(SrvProduct)baseDAO.getById(SrvProduct.class, pkid);
		if(srvProduct!=null){
			baseDAO.delete(srvProduct);
		}
	}

	/* (non-Javadoc)
	 * @see com.soaplat.sysmgr.manageriface.IBaseManager#findAll()
	 */
	public List findAll() {
		List srvProductlist=baseDAO.findAll("SrvProduct","srvprogid");
		return srvProductlist;
	}

	/* (non-Javadoc)
	 * @see com.soaplat.sysmgr.manageriface.IBaseManager#findByProperty(java.lang.String, java.lang.Object)
	 */
	public List findByProperty(String propertyName, Object value) {
		List srvProductlist=baseDAO.findByProperty("SrvProduct", propertyName, value);
		return srvProductlist;
	}

	/* (non-Javadoc)
	 * @see com.soaplat.sysmgr.manageriface.IBaseManager#getById(java.lang.String)
	 */
	public Object getById(String pkid) {
		SrvProduct srvProduct=(SrvProduct)baseDAO.getById(SrvProduct.class, pkid);
		return srvProduct;
	}

	/* (non-Javadoc)
	 * @see com.soaplat.sysmgr.manageriface.IBaseManager#loadById(java.lang.String)
	 */
	public Object loadById(String pkid) {
		SrvProduct srvProduct=(SrvProduct)baseDAO.loadById(SrvProduct.class, pkid);
		return srvProduct;
	}

	/* (non-Javadoc)
	 * @see com.soaplat.sysmgr.manageriface.IBaseManager#save(java.lang.Object)
	 */
	public Object save(Object object) {
		SrvProduct srvProduct = new SrvProduct();
		srvProduct = (SrvProduct)object;
		String strCurMaxPK = baseDAO.getMaxPropertyValue("SrvProduct", "srvprogid");
		String strMaxPK = getcmspk.GetTablePK("SrvProduct", strCurMaxPK);
		srvProduct.setSrvprogid(strMaxPK);
		baseDAO.save(srvProduct);
		//
		return baseDAO.getById(SrvProduct.class, strMaxPK);

	}

	/* (non-Javadoc)
	 * @see com.soaplat.sysmgr.manageriface.IBaseManager#update(java.lang.Object)
	 */
	public void update(Object object) {
		baseDAO.update(object);
		

	}

	/**
	 * Sets the baseDAO.
	 * 
	 * @param baseDAO the new baseDAO
	 */


	/* (non-Javadoc)
	 * @see com.soaplat.sysmgr.manageriface.IBaseManager#save(java.lang.Object[])
	 */
	public void save(Object[] object) {
		if (object.length>0){
			for(int i=0;i<object.length;i++){
				this.save(object[i]);
			}
			
		}
		
	}

	/* (non-Javadoc)
	 * @see com.soaplat.sysmgr.manageriface.IBaseManager#update(java.lang.Object[])
	 */
	public void update(Object[] object) {
		if (object.length>0){
			for(int i=0;i<object.length;i++){
				this.update(object[i]);
			}
			
		}
		
	}

	public List findbyExample(Object exampleentity) {
		List list=baseDAO.findbyExample(exampleentity);
		return list;
	}

	public void setBaseDAO(IBaseDAO baseDAO) {
		this.baseDAO = baseDAO;
	}

	public void setGetcmspk(IGetPK getcmspk) {
		this.getcmspk = getcmspk;
	}

	public List getProductCategoriesBySrvid(String srvid)
	{
		Map map = new HashMap();
		map.put("srvid", srvid);
		List list = baseDAO.queryByNamedQuery("select_ProductCategoriesBySrvid", map);
		return list;
	}
	
	public List getCmsServicesByProductCategoryId(Long productCategoryId)
	{
		Map map = new HashMap();
		map.put("productCategoryId", productCategoryId);
		List list = baseDAO.queryByNamedQuery("select_CmsServicesByProductCategoryId", map);
		return list;
	}
}
